var test = require('tap').test;
var bigint = require('../');

test('PerfectSquares', function (t) {
    var ps = { 0 : true, 1 : true, 4 : true, 9 : true };
    for (var i = 0; i <= 10; i++) {
        t.same(bigint(i).isPerfectSquare(), ps[i] ? true : false);
    }
    
    [ 2, '2', bigint(2), bigint('2') ].forEach(function (two) {
        t.same(
            bigint('12345678987654321').isPerfectSquare(),
            true
        );
    });
    
    t.same(
        bigint('').isPerfectSquare(),
        true
    );
    
    t.end();
});
